import React, { useState, useEffect } from 'react';
import Weather from './Weather';

interface WeatherProps {
  city: string;
  temperature: number;
  weatherDescription: string;
}

const App: React.FC = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState<WeatherProps | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleCityChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCity(event.target.value);
  };

  const fetchWeatherData = async () => {
    setIsLoading(true);
    setError(null);

    try {
      console.log('hello');

      const response = await fetch(`http://localhost:5056/?city=${city}`);


      if (!response.ok) {
        throw new Error(`API request failed with status ${response.status}`);
      }

      const data = await response.json();
      console.log(data.CityName);

      setWeatherData(data);
    } catch (error) {
      if (error instanceof Error) {
        setError(error.message);
      } else {
        console.error('Unknown error:', error);
      }
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (city) {
      fetchWeatherData();
    }
  }, [city]);

  return (
    <div className="App">
      <h1>Weather App</h1>
      <input type="text" value={city} onChange={handleCityChange} placeholder="Enter city name" />
      <button onClick={fetchWeatherData}>Get Weather</button>
      {error && <p className="error">{error}</p>}
      <h2>Weather in {city}</h2>
      {weatherData && <Weather {...weatherData} />}
    </div>
  );
};

export default App;
